import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Bills = () => {
  const location = useLocation();
  const [bills, setBills] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId"); // Get user ID from local storage
    fetchBills(userId); // Fetch bills when component mounts
  }, []);

  const fetchBills = async () => {
    const token = localStorage.getItem("token"); // Retrieve token from local storage
    const userId = localStorage.getItem("userId"); // Retrieve user ID from local storage

    try {
      const response = await fetch(
        `${process.env.REACT_APP_SERVER_DOMIN}/bills/${userId}`,
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`, // Include JWT token in header
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        console.error(errorData.message || "Failed to fetch bills.");
        return;
      }

      const bills = await response.json();
      console.log("Fetched Bills:", bills);
      // Handle bills (e.g., set them in state or dispatch to Redux)
    } catch (error) {
      console.error("Error fetching bills:", error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-bold text-slate-600">Your Bills</h2>
      {bills.length === 0 ? (
        <p>No bills found.</p>
      ) : (
        <div className="mt-4">
          {bills.map((bill) => (
            <div key={bill._id} className="border p-4 mb-4">
              <h3 className="font-bold">Bill ID: {bill._id}</h3>
              <p>Date: {new Date(bill.date).toLocaleString()}</p>
              <p>Total Amount: ₹{(bill.totalAmount / 100).toFixed(2)}</p>
              <h4 className="font-semibold">Items:</h4>
              <ul>
                {bill.items.map((item) => (
                  <li key={item.name}>
                    {item.name} - Qty: {item.qty} - Price: ₹
                    {(item.price / 100).toFixed(2)}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Bills;
