import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

const Bills = () => {
  const [bills, setBills] = useState([]);
  const user = useSelector((state) => state.user);

  useEffect(() => {
    const fetchBills = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_SERVER_DOMIN}/bills/${user._id}`
        );
        setBills(response.data);
      } catch (error) {
        console.error("Error fetching bills:", error);
      }
    };

    if (user._id) {
      fetchBills();
    }
  }, [user]);

  return (
    <div>
      <h1>Your Bills</h1>
      {bills.length === 0 ? (
        <p>No bills found.</p>
      ) : (
        <ul>
          {bills.map((bill) => (
            <li key={bill._id}>
              <h2>Bill ID: {bill._id}</h2>
              <p>Date: {new Date(bill.date).toLocaleString()}</p>
              <p>Total Amount: ₹{bill.totalAmount / 100}</p>
              <h3>Items:</h3>
              <ul>
                {bill.items.map((item) => (
                  <li key={item.name}>
                    {item.name} - Qty: {item.qty} - Price: ₹{item.price / 100}
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Bills;
