// src/pages/OrderHistory.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const userId = localStorage.getItem("userId"); // Retrieve user ID from local storage

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem("token"); // Retrieve JWT from local storage
        const response = await axios.get(`/orders/${userId}`, {
          headers: { Authorization: token },
        });
        setOrders(response.data);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    if (userId) {
      // Ensure userId is available before fetching orders
      fetchOrders();
    }
  }, [userId]);

  const handleOrderClick = (order) => {
    setSelectedOrder(order);
  };

  const handleBackClick = () => {
    setSelectedOrder(null); // Reset selected order to view the list again
  };

  return (
    <div>
      <h1>Order History</h1>
      {!selectedOrder ? (
        <ul>
          {orders.length > 0 ? (
            orders.map((order) => (
              <li key={order._id} onClick={() => handleOrderClick(order)}>
                <h2>Order ID: {order._id}</h2>
                <p>Date: {new Date(order.createdAt).toLocaleString()}</p>
                <p>Total Amount: ₹{order.totalAmount / 100}</p>
                <p>Status: {order.status}</p>
              </li>
            ))
          ) : (
            <p>No orders found.</p>
          )}
        </ul>
      ) : (
        <div>
          <h2>Order Details</h2>
          <button onClick={handleBackClick}>Back to Orders</button>
          <h3>Order ID: {selectedOrder._id}</h3>
          <p>Date: {new Date(selectedOrder.createdAt).toLocaleString()}</p>
          <p>Total Amount: ₹{selectedOrder.totalAmount / 100}</p>
          <h4>Items:</h4>
          <ul>
            {selectedOrder.items.map((item) => (
              <li key={item.name}>
                {item.name} - Qty: {item.qty} - Price: ₹{item.price / 100}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
